#include <iostream>#include <vector>#include <algorithm>using namespace std;struct Produs {    string nume;    double pret;    int stoc;};class Magazin {private:    vector<Produs> produse;public:    void adaugaProdus(const string& nume, double pret, int stoc) {        produse.push_back({nume, pret, stoc});        cout << "Produs adaugat cu succes!\n";    }        void afisareProduse() {        if (produse.empty()) {            cout << "Nu exista produse in magazin.\n";            return;        }        for (const auto& p : produse) {            cout << "Nume: " << p.nume << ", Pret: " << p.pret << ", Stoc: " << p.stoc << "\n";        }    }void cautareProdus(const string& nume) {        for (const auto& p : produse) {            if (p.nume == nume) {                cout << "Produs gasit: Nume: " << p.nume << ", Pret: " << p.pret << ", Stoc: " << p.stoc << "\n";                return;            }        }        cout << "Produsul nu a fost gasit.\n";    }        void vindeProdus(const string& nume, int cantitate) {        for (auto it = produse.begin(); it != produse.end(); ++it) {            if (it->nume == nume) {                if (cantitate > it->stoc) {                    cout << "Stoc insuficient pentru " << nume << "!\n";                    return;                }                it->stoc -= cantitate;                cout << "Produs vandut cu succes!\n";                if (it->stoc == 0) {                    produse.erase(it);                    cout << "Produsul " << nume << " a fost eliminat din magazin.\n";                }                return;            }        }            }void reaprovizioneazaProdus(const string& nume, int cantitate) {        for (auto& p : produse) {            if (p.nume == nume) {                p.stoc += cantitate;                cout << "Produs reaprovizionat cu succes!\n";                return;            }        }        cout << "Produsul nu a fost gasit.\n";    }        void produsMaxMinPret() {        if (produse.empty()) {            cout << "Nu exista produse in magazin.\n";            return;        }        auto maxProd = max_element(produse.begin(), produse.end(), [](const Produs& a, const Produs& b) {            return a.pret < b.pret;        });        auto minProd = min_element(produse.begin(), produse.end(), [](const Produs& a, const Produs& b) {            return a.pret < b.pret;        });        cout << "Produsul cu pretul maxim: " << maxProd->nume << " - " << maxProd->pret << "\n";        cout << "Produsul cu pretul minim: " << minProd->nume << " - " << minProd->pret << "\n";    }void sortareProdusePret() {        sort(produse.begin(), produse.end(), [](const Produs& a, const Produs& b) {            return a.pret < b.pret;        });        cout << "Produsele au fost sortate dupa pret.\n";        afisareProduse();    }};int main() {    Magazin magazin;    int optiune;    string nume;    double pret;    int stoc, cantitate;        do {        cout << "\n1. Adaugare produs\n2. Afisare produse\n3. Cautare produs\n4. Vindere produs\n5. Reaprovizionare produs\n6. Produsul cu cel mai mare/ mic pret\n7. Sortare produse dupa pret\n8. Iesire\nAlege o optiune: ";        cin >> optiune;switch (optiune) {            case 1:                cout << "Introdu numele produsului: ";                cin >> nume;                cout << "Introdu pretul produsului: ";                cin >> pret;                cout << "Introdu stocul produsului: ";                cin >> stoc;                magazin.adaugaProdus(nume, pret, stoc);                break;            case 2:                magazin.afisareProduse();                break;            case 3:                cout << "Introdu numele produsului cautat: ";                cin >> nume;                magazin.cautareProdus(nume);                break;            case 4:                cout << "Introdu numele produsului de vandut: ";                cin >> nume;                cout << "Introdu cantitatea de vandut: ";                cin >> cantitate;                magazin.vindeProdus(nume, cantitate);                break;            case 5:                cout << "Introdu numele produsului de reaprovizionat: ";                cin >> nume;                cout << "Introdu cantitatea de adaugat: ";                cin >> cantitate;                magazin.reaprovizioneazaProdus(nume, cantitate);                break;            case 6:                magazin.produsMaxMinPret();                break;            case 7:                magazin.sortareProdusePret();                break;            case 8:                cout << "Iesire din program.\n";                break;            default:                cout << "Optiune invalida. Reincercati.\n";        }    } while (optiune != 8);        return 0;}